import React, { useState } from 'react';
import '../../App.css';
import './SignUp.css'
import db from '../../firebase/firebase';
import firebase from 'firebase/compat/app'

export default function SignUp() {
  const [input, setInput] = useState("");
  const inputHandler = (e) => {
    setInput(e.target.value);
  };

  const [select, setSelect] = useState("");
  const selectHandler = (e) => {
    setSelect(e.target.value);
  };


  const submitHandler = (e) => {
    e.preventDefault();
    if (input){
      console.log(input);
      //add to firebase
      db.collection("emails").add({
        email: input,
        timestamp: firebase.firestore.FieldValue.serverTime.Timestamp(),
      })
    }
  }
  return (
    <div className='sign-up-container'>
      <section className='sign-up-subscription'>
        <p className='sign-up-subscription-heading'>
          Join the waitlist for Fitless
        </p>
        <p className='sign-up-subscription-text'>
          You can unsubscribe at any time.
        </p>
        <div className='input-areas'>
          <form onSubmit={submitHandler}>
            <input
              className='sign-up-input'
              name='email'
              type='email'
              placeholder='Your Email'
              onChange={inputHandler}
            />
            <select 
              className="sign-up-drop-down"
              onChange ={selectHandler}
              >
              <option selected value="user">User</option>
              <option value="trainer">Trainer</option>
              <option value="fc">Fitness Center</option>
            </select>
            <button className='sign-up-btn--primary--rev' type='submit'>Sign Up</button>
          </form>
        </div>
      </section>
    </div>
  );
}
